mapper.select:
SELECT FILE_ID , FILE_NM , FILE_CONTENT , FILE_FULL_NM , CRT_ID , TO_CHAR(CRT_DT, 'YYYY-MM-DD HH24:MI:SS') AS CRT_DT , CHG_ID , TO_CHAR(CHG_DT, 'YYYY-MM-DD HH24:MI:SS') AS CHG_DT FROM DRAWING
WHERE FILE_ID = #{fileId}
ORDER BY TO_NUMBER(FILE_ID) DESC

SELECT ICON_ID , ICON_NM , ICON_CONTENT , ICON_FULL_NM , CRT_ID , TO_CHAR(CRT_DT, 'YYYY-MM-DD HH24:MI:SS') AS CRT_DT , CHG_ID , TO_CHAR(CHG_DT, 'YYYY-MM-DD HH24:MI:SS') AS CHG_DT FROM ICON
WHERE ICON_ID = #{iconId}
ORDER BY TO_NUMBER(ICON_ID) DESC

SELECT EQP.EQP_ID , EQP.FILE_ID , EQP.ICON_ID , EQP.EQP_NM , EQP.EQP_CONTENT , EQP.EQP_WIDTH , EQP.EQP_HEIGHT , EQP.EQP_X , EQP.EQP_Y , EQP.EQP_VISIBLE , EQP.EQP_ENABLE , EQP.CRT_ID , TO_CHAR(EQP.CRT_DT, 'YYYY-MM-DD HH24:MI:SS') AS CRT_DT , EQP.CHG_ID , TO_CHAR(EQP.CHG_DT, 'YYYY-MM-DD HH24:MI:SS') AS CHG_DT , ICO.ICON_FULL_NM FROM EQUIPMENT EQP INNER JOIN ICON ICO ON EQP.ICON_ID = ICO.ICON_ID INNER JOIN DRAWING DRA ON EQP.FILE_ID = DRA.FILE_ID
WHERE EQP.FILE_ID = #{fileId}
ORDER BY TO_NUMBER(EQP.EQP_ID) DESC

mapper.insert:
INSERT INTO DRAWING
(
	  FILE_ID
	, FILE_NM
	, FILE_CONTENT
	, FILE_FULL_NM
	, CRT_ID
	, CRT_DT
	, CHG_ID
	, CHG_DT
)
VALUES 
(
	  (SELECT NVL(MAX(TO_NUMBER(FILE_ID)) + 1, 1) AS FILE_ID FROM DRAWING)
	, #{fileNm}
	, #{fileContent}
	, #{fileFullNm}
	, #{crtId}
	, SYSDATE
	, #{chgId}
	, SYSDATE
)

INSERT INTO ICON 
(
	ICON_ID
	, ICON_NM
	, ICON_CONTENT
	, ICON_FULL_NM
	, CRT_ID
	, CRT_DT
	, CHG_ID
	, CHG_DT
)
VALUES 
(
	(SELECT NVL(MAX(TO_NUMBER(ICON_ID)) + 1, 1) AS ICON_ID FROM ICON)
	, #{iconNm}
	, #{iconContent}
	, #{iconFullNm}
	, #{crtId}
	, SYSDATE
	, #{chgId}
	, SYSDATE
)

INSERT INTO EQUIPMENT 
(
	EQP_ID
	, FILE_ID
	, ICON_ID
	, EQP_NM
	, EQP_CONTENT
	, EQP_WIDTH
	, EQP_HEIGHT
	, EQP_X
	, EQP_Y
	, EQP_VISIBLE
	, EQP_ENABLE
	, CRT_ID
	, CRT_DT
	, CHG_ID
	, CHG_DT
)
VALUES 
(
	(SELECT NVL(MAX(TO_NUMBER(EQP_ID)) + 1, 1) AS EQP_ID FROM EQUIPMENT)
	, #{fileId}
	, #{iconId}
	, #{eqpNm}
	, #{eqpContent}
	, #{eqpWidth}
	, #{eqpHeight}
	, #{eqpX}
	, #{eqpY}
	, #{eqpVisible}
	, #{eqpEnable}
	, #{crtId}
	, SYSDATE
	, #{chgId}
	, SYSDATE
)

mapper.update:
UPDATE EQUIPMENT
SET EQP_X = #{eqpX}
	, EQP_Y = #{eqpY}
	, CHG_ID = #{chgId}
	, CHG_DT = SYSDATE
WHERE EQP_ID = #{eqpId}
